        RES.ASSIGNEE_ IS NULL
        AND I.TYPE_ = 'candidate'
        AND ( I.USER_ID_ = #{userId} ))

            OR (
             I.TYPE_ = 'notify'
            AND ( I.USER_ID_ = #{userId} ))

